function Screen1({ feed, upvotePostAction }) {
	const [mode, setMode] = useState(1)
	const { colors } = useTheme()
	const navigation = useNavigation()

	return (
		<SafeAreaView>
			<Fab
				renderInPortal={false}
				isDisabled={false}
				onPress={() => {
					navigation.navigate("Create Post")
				}}
				position="absolute"
				style={{ marginBottom: -15 }}
				icon={<Icon as={Ionicons} name="add" />}
			/>
			<VStack>
				<HStack justifyContent="center" space={2}>
					<TouchableOpacity
						onPress={() => {
							setMode(0)
						}}
					>
						<Heading
							underline={mode === 0}
							color={mode === 0 ? colors.primary["500"] : colors.muted["500"]}
						>
							Following
						</Heading>
					</TouchableOpacity>
					<Heading>|</Heading>
					<TouchableOpacity
						onPress={() => {
							setMode(1)
						}}
					>
						<Heading
							underline={mode === 1}
							color={mode === 1 ? colors.primary["500"] : colors.muted["500"]}
						>
							Explore
						</Heading>
					</TouchableOpacity>
				</HStack>
				<ScrollView
					style={{
						marginTop: 10,
						height: Dimensions.get("window").height * 0.8,
					}}
				>
					<VStack space={5}>
						{mode === 0 &&
							feed.map((item, index) => (
								<Post
									upvotePostAction={upvotePostAction}
									{...item}
									key={index}
								/>
							))}
						{mode === 1 &&
							dummyData.map((item, index) => (
								<Post
									upvotePostAction={upvotePostAction}
									{...item}
									key={index}
								/>
							))}
					</VStack>
				</ScrollView>
			</VStack>
		</SafeAreaView>
	)
}

const mapStateToProps = (state) => ({
	feed: state.feed,
})
const mapDispatchToProps = (dispatch) => ({
	testingAction: () => dispatch(testing()),
	upvotePostAction: (uid, postId, downvote) =>
		dispatch(upvotePost(uid, postId, downvote)),
})

export default connect(mapStateToProps, mapDispatchToProps)(Screen1)

